<script lang="ts">
import { mapActions, mapState } from "vuex";
import validatorMixin from '@/mixins/validator'
import mixins from 'vue-typed-mixins'
import Alert from '@/components/Alert.vue'

export default mixins(validatorMixin).extend({
  name: 'RegisterPage',
  layout: 'blank',
  components: { Alert },
  middleware: ['guest'],
  head() {
    return {
      title: "Register - Eclairia",
      meta: [
        // hid is used as unique identifier. Do not use `vmid` for it as it will not work
        {
          hid: "RegisterPage",
          name: "RegisterPage",
          content: "Inscrivez-vous à votre session",
        },
      ],
    };
  },
  data() {
    return {
      formUser: {
        email: '' as string,
        username: '' as string,
        password: '' as string,
        password_confirm: '' as string,
        default_language: 1,
        user_location: 1,
        email_source: 1,
        screen_name: '' as string | null,
        accept_tos: false
      },
      errors: {
        email: '' as string,
        username: '' as string,
        password: '' as string,
        message: '' as string,
        password_confirm: '' as string
      },
      typeAlert: 'info',
      successRegister: false
    };
  },
  computed: {
    ...mapState({
      loading(state: any) {
        return state.user.loading
      }
    })
  },
  methods: {
    ...mapActions({
      registerUser: 'user/register'
    }),
    register() {
      try {
        this.validateInputs()

        if (!this.errors.message) {
          this.formUser.screen_name = this.formUser.username
          this.registerUser(this.formUser)
            .then((res) => {
              if (res.status === 201) {
                this.$toast.success('Compte créé avec succès', {
                  icon: 'check',
                  duration: 4000,
                  onComplete: () => {
                    this.showToastRedirectToLogin()
                    this.$router.push('/login')
                  }
                })
              }
            })
            .catch((err) => {
              this.catchErrors(err)

              this.$toast.error(this.errors.message, {
                icon: 'circle-exclamation',
                duration: 8000
              })
            })
        }
      } catch (error) {
        if (!navigator.onLine) {
          this.errors.message = "Vous n'êtes pas connecté à Internet !"
          return
        }

        this.errors.message = "Une erreur est survenue, réessayez svp.";
      }
    },

    /**
     * Allow track errors when registration failed
     * @param {any} err
     * @return void 
     */
    catchErrors(err: any) : void {
      let errors: any = { ...this.errors }

      if (err && err.status === 400 && err.data) {
        for (const er in err.data) {
          if (errors.hasOwnProperty(er)) {
            errors[er] = err.data[er].join(', ');
          }
        }
      } else if (
        err &&
        (err.status === 409 || (err.data && err.data.error && Array.isArray(err.data.detail) && err.data.detail.length))
      ) {
        errors = this.catchConflitErrors(errors, err)
      } else if (err && err.data && err.data.error) {
        errors.message = err.data.error
      } else if (err && err.message) {
        errors.message = err.message
      } else {
        errors.message = 'Erreur inconnue lors de l’inscription.'
      }

      this.errors = errors
      this.trackMessageErrors()
    },

    /**
     * Catch errors conflits of fields used by other users
     * @param {any} defaultErrors the errors exited in this.errors
     * @param {any} httpError object content HTTP errors responded
     * @return {any}
     */ 
    catchConflitErrors(defaultErrors: any, httpError: any) : any {
      if (httpError.status === 409 || (httpError.data.error && httpError.data.detail && httpError.data.detail.length)) {
        for (const detailErr of httpError.data.detail) {
          if (detailErr.toLowerCase().includes('email')) {
            defaultErrors.email = this.concatError(defaultErrors.email, 'Email déjà utilisé')
          }
          if (detailErr.toLowerCase().includes('username')) {
            defaultErrors.username = this.concatError(defaultErrors.username, 'Nom d\'utilisateur déjà utilisé')
          }
        }
      }
      return defaultErrors
    },
    concatError(defaulErrorValue: string, newErrorValue: string): string {
      return defaulErrorValue ? defaulErrorValue + ', ' + newErrorValue : newErrorValue
    },
    validateInputs(field?: string) {
      if (field === 'email') {
        this.errors.email = !this.isEmail(this.formUser.email)
          ? 'Email invalide'
          : ''
      }

      if (field === 'username') {
        this.errors.username = this.formUser.username.length < 4
          ? "Nom d'utilisateur minimum 4 caractères"
          : ''
      }

      if (field === 'password') {
        this.errors.password = this.formUser.password.length < 8
          ? "Mot de passe minimum 8 caractères"
          : ''
      }

      if (field === 'password_confirm' || field === 'password') {
        this.validatePasswordConfirm()
      }

      if (!field) {
        this.trackMessageErrors()
      }
    },
    validatePasswordConfirm() {
      this.errors.password_confirm = this.formUser.password_confirm !== this.formUser.password
        ? 'Mot de passe ne correspond pas'
        : ''
    },
    trackMessageErrors() {
      if (this.errors.email || this.errors.password || this.errors.username) {
        this.errors.message = Object
          .values(this.errors)
          .filter(e => e.length)
          .join(', ')
      }

      if (this.errors.message) {
        for (const field in this.errors) {
          const inputfield: HTMLInputElement | null = document.querySelector('#' + field)

          if (inputfield) {
            if ({ ...this.errors }[field]?.length) {
              inputfield.focus()
              break
            }
          }
        }
      }
    },
    getErrors() {
      return this.errors.message.split(', ')
    },
    showToastRedirectToLogin() {
      this.$toast.show('Veuillez vous connecter avec votre compte', {
        duration: 8000,
        type: 'info',
        icon: 'circle-exclamation',
        action: {
          text: 'Ok',
          onClick(e: any, toastObject: any) {
            toastObject.goAway(0)
          }
        }
      })
    }
  },
});
</script>
